CREATE FUNCTION pointsToWinSet AS (p1Score, p2Score, assumeWinNextGame) -> multiIf(p2Score = 5, (7 - (p1Score + assumeWinNextGame)) * 4, ((p1Score + assumeWinNextGame) = 7) AND (p2Score = 6), 0, p2Score = 6, ((6 - (p1Score + assumeWinNextGame)) * 4) + 7, (p1Score + assumeWinNextGame) = 7, 0, ((p1Score + assumeWinNextGame) = 6) AND (p2Score < 5), 0, (6 - (p1Score + assumeWinNextGame)) * 4)
